import React, { useState } from "react";
import { Search, Filter, Grid, List, Plus } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { SlidingBanner } from "@/components/hero/SlidingBanner";
import { Link, useNavigate } from "react-router-dom";

const Index = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  const topRegions = [
    {
      name: "Mumbai",
      image:
        "https://www.trawell.in/admin/images/upload/955980144Mumbai_Gateway_India_Main.jpg",
    },
    {
      name: "Hyderabad",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "Delhi",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "Nagpur",
      image:
        "data:image/jpeg;base64,",
    },
    {
      name: "Pune",
      image:
        "https://www.tusktravel.com/blog/wp-content/uploads/2019/09/Lohagad-Fort-Pune.jpg",
    },
  ];



  // Filter topRegions based on searchTerm (case-insensitive)
  const filteredRegions = topRegions.filter((region) =>
    region.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-black text-white">
      {/* Header */}

      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Keep existing SlidingBanner component */}
        <SlidingBanner />

        {/* Search Bar */}
        <div className="mb-4 sm:mb-6">
          <div className="relative">
            <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              type="text"
              placeholder="Search destinations, trips, or experiences..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-black border border-gray-700 rounded-lg pl-12 pr-4 py-3 text-sm sm:text-base text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-[#D8B4FE] focus:border-transparent transition-all"
            />
          </div>
        </div>

        {/* Filter Controls */}
        <div className="flex flex-wrap items-center gap-3 mb-8">
          <Button
            variant="outline"
            className="bg-black border-gray-700 text-white hover:bg-gray-800 hover:border-[#D8B4FE] transition-all"
          >
            <Grid className="w-4 h-4 mr-2" />
            Group by
          </Button>
          <Button
            variant="outline"
            className="bg-black border-gray-700 text-white hover:bg-gray-800 hover:border-[#D8B4FE] transition-all"
          >
            <Filter className="w-4 h-4 mr-2" />
            Filter
          </Button>
          <Button
            variant="outline"
            className="bg-black border-gray-700 text-white hover:bg-gray-800 hover:border-[#D8B4FE] transition-all"
          >
            <List className="w-4 h-4 mr-2" />
            Sort by...
          </Button>
        </div>

        {/* Top Regional Selections */}
        <section className="mb-10">
          <h2 className="text-xl sm:text-2xl font-bold mb-4 sm:mb-6 text-[#e6e6fa]">
            Top Regional Selections
          </h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-5 gap-4 sm:gap-6">
            {filteredRegions.length === 0 ? (
              <p className="text-white text-center col-span-full">
                No matching regions found.
              </p>
            ) : (
              filteredRegions.map((region) => (
                <Card
                  key={region.name}
                  className="group relative overflow-hidden bg-white rounded-2xl border-0 shadow-lg hover:shadow-2xl transition-all duration-500 transform hover:scale-105 cursor-pointer"
                  onClick={() => {
                    if (region.name === "Mumbai") {
                      navigate("/itinerary/mumbai");
                    } else if (region.name === "Hyderabad") {
                      navigate("/itinerary/hyderabad");
                    }
                  }}
                >
                  <CardContent className="p-0">
                    {/* Background Image */}
                    <div className="relative w-full h-48 sm:h-52 overflow-hidden">
                      <img
                        src={region.image}
                        alt={region.name}
                        className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                      />
                      {/* Gradient Overlay */}
                      <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent" />
                      
                      {/* Region Name - Stylized like Kerala image */}
                      <div className="absolute top-4 left-0 right-0 text-center">
                        <h3 className="text-lg sm:text-xl font-bold text-white drop-shadow-lg tracking-wide font-serif">
                          {region.name}
                        </h3>
                      </div>
                      

                    </div>
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        </section>

        {/* Plan New Trip Section */}
        <section className="mb-10">
          <div className="text-center">
            <h2 className="text-xl sm:text-2xl font-bold mb-4 sm:mb-6 text-[#D3D3FF]">
              Ready to Plan Your Next Adventure?
            </h2>
            <p className="text-gray-300 mb-6 text-sm sm:text-base">
              Create personalized itineraries and discover amazing destinations
            </p>
            <Button
              asChild
              className="bg-[#d3d3ff] text-black px-8 py-4 rounded-lg shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300 text-lg font-semibold"
            >
              <Link to="/create-trip">
                <Plus className="w-6 h-6 mr-3" />
                Plan New Trip
              </Link>
            </Button>
          </div>
        </section>
      </main>


    </div>
  );
};

export default Index;
